import { useCallback, useEffect, useState } from 'react'
import { Table, Input, ConfigProvider, Image } from 'antd'
import { SearchOutlined } from '@ant-design/icons'
import type { ColumnsType } from 'antd/es/table'
import zhCN from 'antd/es/locale/zh_CN'

import dayjs from 'dayjs'

import { getUsers } from '@/api/base'

import './index.scss'

export default function UsersInfo() {
  const [users, setUsers] = useState([])
  const [uname, setUname] = useState('')
  const [currentPage, setCurrentPage] = useState(1)
  const [pageSize, setPageSize] = useState(10)
  const [totalCount, setTotalCount] = useState(0)

  const loadUsers = useCallback(async () => {
    const { data: res } = await getUsers({ uname, currentPage, pageSize })
    console.log(res)
    if (res.msg === 'ok') {
      setUsers(res.data.rows)
      setTotalCount(res.data.count)
    }
  }, [uname, currentPage, pageSize])
  useEffect(() => {
    loadUsers()
  }, [loadUsers])

  const columns: ColumnsType<any> = [
    {
      title: '头像',
      dataIndex: 'avatar',
      align: 'center',
      render: (value: string) => {
        return (
          <Image
            width={65}
            height={65}
            preview={false}
            src={'/uploads' + value}
            fallback="data:image/png;base64,"
          />
        )
      },
    },
    {
      title: '用户名',
      dataIndex: 'uname',
      align: 'center',
    },
    {
      title: '昵称',
      dataIndex: 'nickname',
      align: 'center',
      render: (value: string) => {
        return value || '暂无'
      },
    },
    {
      title: '年龄',
      dataIndex: 'age',
      align: 'center',
      render: (value: number) => {
        return value || '暂无'
      },
    },
    {
      title: '性别',
      dataIndex: 'gender',
      align: 'center',
      render: (value: string) => {
        return value || '其他'
      },
    },
    {
      title: '是否可用',
      dataIndex: 'state',
      align: 'center',
      render: (value: string) => {
        return value === '1' ? '正常' : '已被冻结'
      },
    },
    {
      title: '注册时间',
      dataIndex: 'createdTime',
      align: 'center',
      render: (value) => {
        return dayjs(value).format('YYYY/MM/DD HH:mm:ss')
      },
    },
    {
      title: '申请注销账号时间',
      dataIndex: 'logout_account_time',
      align: 'center',
      render: (value) => {
        return value ? dayjs(value).format('YYYY/MM/DD HH:mm:ss') : '无'
      },
    },
  ]

  return (
    <div className="users-info">
      {/* 操作区域 */}
      <div style={{ marginBottom: 10 }}>
        <Input
          style={{ width: 380 }}
          value={uname}
          onChange={({ target: { value } }) => setUname(value)}
          prefix={<SearchOutlined />}
          placeholder="请输入用户名"
        />
      </div>
      <ConfigProvider locale={zhCN}>
        <Table
          columns={columns}
          rowKey={(record: any) => record.uname}
          locale={{ emptyText: '暂无数据' }}
          dataSource={users}
          pagination={{
            total: totalCount,
            showSizeChanger: true,
            onChange: (page: number) => setCurrentPage(page),
            onShowSizeChange: (_, pageSize) => setPageSize(pageSize),
          }}
        />
      </ConfigProvider>
    </div>
  )
}
